import { Component } from '@angular/core';
import { IonicPage, NavController, NavParams,ViewController,AlertController,LoadingController } from 'ionic-angular';
import { Camera } from '@ionic-native/camera';
import { AndroidPermissions } from '@ionic-native/android-permissions';


/**
 * Generated class for the ModalPage page.
 *
 * See https://ionicframework.com/docs/components/#navigation for more info on
 * Ionic pages and navigation.
 */

@IonicPage()
@Component({
  selector: 'page-modal',
  templateUrl: 'modal.html',
})
export class ModalPage {


   base64Image:any;
   sourceSelection
   scene;
   slides = [];
   text;
   

  constructor(public navCtrl: NavController,private androidPermissions: AndroidPermissions, public navParams: NavParams,public viewCtrl: ViewController,public camera:Camera, public alertCtrl: AlertController,public loadingCtrl: LoadingController) {

  	this.scene = this.navParams.get('scene');
   
      console.log(this.scene);
      this.slides = this.scene.scene.slides;
      this.text = this.scene.text;

      alert(JSON.stringify(this.slides));
      
      console.log('ionViewDidLoad DiseasePage');
      this.androidPermissions.checkPermission(this.androidPermissions.PERMISSION.CAMERA).then(
    success => console.log('Permission granted'),
    err => this.androidPermissions.requestPermissions([this.androidPermissions.PERMISSION.CAMERA,this.androidPermissions.PERMISSION.READ_EXTERNAL_STORAGE])
  );

  }


   forbrowser(source){


      this.scene.scene.slides.push({image:"data:image/jpeg;base64,"})



   }

    takePicture(source){
     
          let loading = this.loadingCtrl.create({
            content: `<div class="custom-spinner-container">
                        <div class="custom-spinner-box">Loading Image</div>
                     </div>`
          });

          loading.present();

             
         if(source=="camera"){
            this.sourceSelection = this.camera.PictureSourceType.CAMERA;
         }else if(source=="gallery"){
            this.sourceSelection = this.camera.PictureSourceType.PHOTOLIBRARY;
            // alert(source);
        }
          this.camera.getPicture({
            sourceType:this.sourceSelection,
              destinationType: this.camera.DestinationType.DATA_URL,
              encodingType: this.camera.EncodingType.JPEG,
              mediaType: this.camera.MediaType.PICTURE
        //      targetWidth: 1000,
        //      targetHeight: 1000
          }).then((imageData) => {
            // imageData is a base64 encoded string
              this.base64Image = "data:image/jpeg;base64," + imageData;
              // alert(this.base64Image);
              this.scene.scene.slides.push({image:this.base64Image});
              loading.dismiss();
           
          }, (err) => {
              console.log(err);
              alert(err);
              loading.dismiss();
          });
    }

  ionViewDidLoad() {
    console.log('ionViewDidLoad ModalPage');
  }

  cancel() {
    this.viewCtrl.dismiss();
  }

  deleteImage(index){

     console.log(index);

     this.scene.scene.slides.splice(index,1);
  }
 
  save() {
    
    // if(this.base64Image){
      this.scene.text = this.text;
      this.viewCtrl.dismiss({scene: this.scene});  
    // }else{

      // let alert = this.alertCtrl.create({
      //   title: 'No image selected',
      //   subTitle: 'Select an image',
      //   buttons: ['Dismiss']
      // });
      // alert.present();

    // }

    
  }

}
